sys_script_include.script:
var GeolocationProcessor = Class.create();
GeolocationProcessor.prototype = Object.extendsObject(new AbstractAjaxProcessor(), {

	    storeLocation: function() {
		        var lat = this.getParameter('lat');
				        var lon = this.getParameter('lon');

								        // Example: Storing the location in a custom table
										        var locationGR = new GlideRecord('u_location_table'); // Replace 'u_location_table' with your custom table name
												        locationGR.initialize();
														        locationGR.u_latitude = lat;
																        locationGR.u_longitude = lon;
																		        locationGR.insert();

																						        return 'Location stored successfully';
																								    }

																									});
